// function to get instances (instances must be an array)
function getInstances(instances, condition)
{
	return instances.filter(condition);
}


//-------------------------------------------------------------------------------


// get instances
const view = runtime.objects.uiScrollView.getFirstPickedInstance();
const vVars = view.instVars;
const vBox = view.getBoundingBox();
const allArr = [];
let objArr = Array.from(runtime.objects.uiScrollComponent.getAllInstances());

// filter objArr to all uiScrollComponents that are linked to this view
objArr = objArr.filter(
instance => { 
	// test instance variable has this view ID
	return instance.instVars.scrollViewID === vVars.scrollViewID; 
});


//-------------------------------------------------------------------------------


// default bounds to infinity
vVars.scrollX1 = Infinity;
vVars.scrollY1 = Infinity;
vVars.scrollX2 = -Infinity;
vVars.scrollY2 = -Infinity;


//-------------------------------------------------------------------------------


// go through every component
for (const obj of objArr)
{
	// collection of obj + children
	const instArr = Array.from(obj.allChildren());
	instArr.push(obj);


	// now go through children
	for (const child of instArr) 
	{	

		// get bounding box as DOMRect
		const rect = child.getBoundingBox();

		// bounds
		vVars.scrollX1 = Math.min(vVars.scrollX1, rect.left);
		vVars.scrollY1 = Math.min(vVars.scrollY1, rect.top);
		vVars.scrollX2 = Math.max(vVars.scrollX2, rect.right);
		vVars.scrollY2 = Math.max(vVars.scrollY2, rect.bottom);


		// if this instance isn't in the all array already...
		if (!allArr.some(item => item === child))
		{
			// add this child to all array
			allArr.concat(instArr);
		}
	}

	// add this parent object to all array
	allArr.push(obj);
}


//-------------------------------------------------------------------------------


// add margins
vVars.scrollX1 -= vVars.scrollMargins;
vVars.scrollY1 -= vVars.scrollMargins;
vVars.scrollX2 += vVars.scrollMargins;
vVars.scrollY2 += vVars.scrollMargins;


// convert bounds to offsets from view
vVars.scrollX1 -= vBox.left;
vVars.scrollY1 -= vBox.top;
vVars.scrollX2 -= vBox.left;
vVars.scrollY2 -= vBox.top;


// get offset of bounds before normalizing them
const x1 = vVars.scrollX1;
const y1 = vVars.scrollY1;


// normalize these values
vVars.scrollX2 += x1;
vVars.scrollY2 += y1;
vVars.scrollX1 += x1;
vVars.scrollY1 += y1;


//-------------------------------------------------------------------------------


// go through every instance in the all array
for (const obj of allArr)
{		
	// move objects to normalize top left (allign with view)
	obj.x -= x1;
	obj.y -= y1;

	// test if this uiScrollComponent (cause it has the variable, no other way to know what family afaik) is allowed to move position
	if (obj.instVars.scrollAffectPosition === true)
	{	
		// set offsets
		obj.instVars.scrollOffsetX = obj.x - vBox.left;
		obj.instVars.scrollOffsetY = obj.y - vBox.top;
	}
}


//-------------------------------------------------------------------------------


// lastly we need to update the handles tied to this view
const handleArr = runtime.objects.uiSliderHandle.getAllInstances();

// filter objArr to all uiScrollComponents that are linked to this view
const handles = 
[ 
	['x', getInstances(handleArr, 
		instance => { return (instance.instVars.ID === vVars.scrollXHandleID); }) [0]],
	['y', getInstances(handleArr, 
		instance => { return (instance.instVars.ID === vVars.scrollYHandleID); }) [0]]
];


//-------------------------------------------------------------------------------


// for each slider handle (up to two)
for (const handleElement of handles)
{
	// the handle instance
	const handle = handleElement[1];
	const axis = handleElement[0];

	if (!(handle === undefined))
	{
		// get the slider bar instVars that this handle is linked to
		const barVars = 
			getInstances(runtime.objects.uiSliderBar.getAllInstances(), 
			instance => { return (instance.instVars.ID === handle.instVars.sliderHandleAttachedBar); })
			[0].instVars;

		// will need handle instance vars
		const handleVars = handle.instVars;


		// set properties
		barVars.sliderMin = 0;

		// set sliderMax based on axis
		if (axis === 'x') { barVars.sliderMax = -(vVars.scrollX2 - vVars.scrollX1) + view.width; }
		else if (axis === 'y') { barVars.sliderMax = -(vVars.scrollY2 - vVars.scrollY1) + view.height; }

		// set the slider steps based on scrollSpeed, but adjusted to have no remainder when divided by range
		barVars.sliderStep = 
			handleVars.scrollSpeed + (Math.abs(barVars.sliderMax - barVars.sliderMin) % 
			handleVars.scrollSpeed) / Math.floor(Math.abs(barVars.sliderMax - barVars.sliderMin) / 
			handleVars.scrollSpeed);

		// set slider min
		handleVars.sliderHandleValue = barVars.sliderMin;


		// call aux function to update, hopefully can find better solution later
		Function.call("uiScrollRecalculationAux [INTERNAL]", handle.uid);
	}
}


//-------------------------------------------------------------------------------


// update UI
Function.call("uiUpdate [INTERNAL]");

actions.script:
// get instances
const pickedObj = runtime.objects.uiScrollComponent.getFirstPickedInstance();
const children = pickedObj.allChildren();
const objs = [];

// layer name
const vVars = runtime.objects.uiScrollView.getFirstPickedInstance().instVars;
const layer = runtime.layout.getLayer(vVars.scrollViewOriginalLayer + "[ScrollComponent/" + vVars.scrollViewID + "]");

// get children in the objs array
objs.push(pickedObj);
for (const child of children) { objs.push(child); }


// move all objects to new layer
for (const obj of objs)
{
	obj.moveToLayer(layer);
}

const caroVars = runtime.objects.uiCarousel.getFirstPickedInstance().instVars;

try {
	runtime.callFunction("carousel " + caroVars.ID, caroVars.carouselIndex);
} 
catch(error) {/* nothing, just ignore it*/}

const handleVars = runtime.objects.uiSliderHandle.getFirstPickedInstance().instVars;

try {
	runtime.callFunction("slider " + handleVars.ID, handleVars.sliderHandleValue);
} 
catch(error) {/* nothing, just ignore it*/}

const checkboxVars = runtime.objects.uiCheckbox.getFirstPickedInstance().instVars;

try {
	runtime.callFunction("checkbox " + checkboxVars.ID, checkboxVars.uiChecked);
} 
catch(error) {/* nothing, just ignore it*/}

try {
	runtime.callFunction("select " + 
		runtime.objects.ui.getFirstPickedInstance().instVars.ID);
} 
catch(error) {/* nothing, just ignore it*/}
