actions.script:
// first, get this instance and its parent
const canvasPaste = runtime.objects.uiCanvasPaste.getFirstPickedInstance();
const children = canvasPaste.children();

// then set the offset variables based on position difference
for (const child of children)
{
	child.isVisible = false;
}

// first, get this instance and its parent
const scrollComponent = runtime.objects.uiScrollComponent.getFirstPickedInstance();
const canvas = runtime.objects.uiCanvas.getFirstPickedInstance();

// then set the offset variables based on position difference
scrollComponent.instVars.scrollOffsetX -= canvas.x;
scrollComponent.instVars.scrollOffsetY -= canvas.y;

// first, get this instance and its parent
const scrollComponent = runtime.objects.uiScrollComponent.getFirstPickedInstance();
//const parent = runtime.getInstanceByUid(scrollComponent.instVars.scrollParentUID);
const canvas = runtime.objects.uiCanvas.getFirstPickedInstance();

// set position of scroll component
localVars.targetPos = canvas.y + scrollComponent.instVars.scrollOffsetY + localVars.sliderValue;

// get instances
const components = runtime.objects.uiScrollComponent.pickedInstances();

// go through every component
for (const component of components)
{
	// get children
	const children = component.allChildren();

	// create an array that contains the component AND its children
	const objects = Array.from(children);
	objects.push(component);

	for (const obj of objects)
	{	
		// get bounding box as DOMRect
		const rect = obj.getBoundingBox();

		// bounds
		if (localVars.x1 > rect.left) { localVars.x1 = rect.left; }
		if (localVars.y1 > rect.top) { localVars.y1 = rect.top; }
		if (localVars.x2 < rect.right) { localVars.x2 = rect.right; }
		if (localVars.y2 < rect.bottom) { localVars.y2 = rect.bottom; }
	}
}

// get instances
const canvas = runtime.objects.uiCanvas.getFirstPickedInstance();
const pasteArr = Array.from(runtime.objects.uiCanvasPaste.getPickedInstances());

// iterate through all objects to be pasted
for (const pasteObj of pasteArr)
{
	// now go through children
	for (const child of pasteObj.allChildren()) 
	{
		// if this child isn't in the array already...
		if (!pasteArr.some(item => item === child))
		{
			// paste this child
			pasteArr.push(child);
		}
	}
}

// sort array by zIndex
pasteArr.sort((a, b) => a.zIndex - b.zIndex);

// paste all objects
for (const pasteObj of pasteArr)
{
	// paste this obj
	canvas.pasteInstances([pasteObj]);
}
