// hijack draw function
const oldDraw = C3.Plugins.Sprite.Instance.prototype.Draw;

C3.Plugins.Sprite.Instance.prototype.Draw = function(renderer) {

	// inst is the instance iterated in this function, sorted by z layer, and only includes instances seeable within the viewport
    const inst = runtime.getInstanceByUid(this._inst._uid);

	// if a scissor is reached in the z order
    if (inst.objectType.name === "uiScissor") 
	{	
		// get the scroll position
		const vW = runtime.viewportWidth / runtime.layout.scale / inst.layer.scale,
			  vH = runtime.viewportHeight / runtime.layout.scale / inst.layer.scale,
			  sX = runtime.layout.scrollX - vW / 2,
			  sY = runtime.layout.scrollY - vH / 2;

		// get various vars relating to screen position (css pixels)
		const pos = inst.layer.layerToCssPx(inst.x - sX, inst.y - sY),
			  size = inst.layer.layerToCssPx(inst.width, inst.height),
			  min = inst.layer.layerToCssPx(0, 0);

		// create scissor
		if (true)
			renderer.SetScissorRect((pos[0] - min[0]) * devicePixelRatio,
									(pos[1] - min[1]) * devicePixelRatio,
									(size[0] - min[0]) * devicePixelRatio,
									(size[1] - min[1]) * devicePixelRatio);
    } 

	// if a scissor end is reached in the z order, remove the scissor from the renderer so objects render as normal
	else if (inst.objectType.name === "uiScissorEnd") 
	{
        renderer.RemoveScissorRect();
    } 

	// normal objects
	else
	{
		oldDraw.call(this, renderer);
    }
};

