// function to get instances (instances must be an array)
function getInstances(instances, condition)
{
	return instances.filter(condition);
}


//-------------------------------------------------------------------------------


// get instances
const canvas = runtime.objects.uiCanvas.getFirstPickedInstance();
const cVars = canvas.instVars;
const allArr = [];
let objArr = Array.from(runtime.objects.uiScrollComponent.getAllInstances());

// filter objArr to all uiScrollComponents that are linked to this canvas
objArr = objArr.filter(
instance => { 
	// test instance variable has this canvas ID
	return instance.instVars.scrollCanvasID === cVars.canvasID; 
});


//-------------------------------------------------------------------------------


// default bounds to infinity
cVars.scrollerX1 = Infinity;
cVars.scrollerY1 = Infinity;
cVars.scrollerX2 = -Infinity;
cVars.scrollerY2 = -Infinity;


//-------------------------------------------------------------------------------


// go through every component
for (const obj of objArr)
{
	// collection of obj + children
	const instArr = Array.from(obj.allChildren());
	instArr.push(obj);

	// if set to be canvas-masked, set entire collection to invisible
	const visibility = !obj.instVars.scrollCanvasMask;


	// now go through children
	for (const child of instArr) 
	{	

		// get bounding box as DOMRect
		const rect = child.getBoundingBox();

		// bounds
		cVars.scrollerX1 = Math.min(cVars.scrollerX1, rect.left);
		cVars.scrollerY1 = Math.min(cVars.scrollerY1, rect.top);
		cVars.scrollerX2 = Math.max(cVars.scrollerX2, rect.right);
		cVars.scrollerY2 = Math.max(cVars.scrollerY2, rect.bottom);



		// set visibility
		child.isVisible = visibility;


		// if this instance isn't in the all array already...
		if (!allArr.some(item => item === child))
		{
			// add this child to all array
			allArr.concat(instArr);
		}
	}

	// add this parent object to all array
	allArr.push(obj);
}


//-------------------------------------------------------------------------------


// add margins
cVars.scrollerX1 -= cVars.scrollerMargins;
cVars.scrollerY1 -= cVars.scrollerMargins;
cVars.scrollerX2 += cVars.scrollerMargins;
cVars.scrollerY2 += cVars.scrollerMargins;


// convert bounds to offsets from canvas
cVars.scrollerX1 -= canvas.x;
cVars.scrollerY1 -= canvas.y;
cVars.scrollerX2 -= canvas.x;
cVars.scrollerY2 -= canvas.y;


// get offset of bounds before normalizing them
const x1 = cVars.scrollerX1;
const y1 = cVars.scrollerY1;


// normalize these values
cVars.scrollerX2 += x1;
cVars.scrollerY2 += y1;
cVars.scrollerX1 += x1;
cVars.scrollerY1 += y1;


//-------------------------------------------------------------------------------


// go through every instance in the all array
for (const obj of allArr)
{		
	// move objects to normalize top left (allign with canvas)
	obj.x -= x1;
	obj.y -= y1;

	// test if this uiScrollComponent (cause it has the variable, no other way to know what family afaik) is allowed to move position
	if (obj.instVars.scrollAffectPosition === true)
	{	
		// set offsets
		obj.instVars.scrollOffsetX = obj.x - canvas.x;
		obj.instVars.scrollOffsetY = obj.y - canvas.y;
	}
}


//-------------------------------------------------------------------------------


// lastly we need to update the handles tied to this canvas
const handleArr = runtime.objects.uiSliderHandle.getAllInstances();

// filter objArr to all uiScrollComponents that are linked to this canvas
const handles = 
[ 
	['x', getInstances(handleArr, 
		instance => { return (instance.instVars.ID === cVars.scrollXHandleID); }) [0]],
	['y', getInstances(handleArr, 
		instance => { return (instance.instVars.ID === cVars.scrollYHandleID); }) [0]]
];


//-------------------------------------------------------------------------------


// for each slider handle (up to two)
for (const handleElement of handles)
{
	// the handle instance
	const handle = handleElement[1];
	const axis = handleElement[0];

	if (!(handle === undefined))
	{
		// get the slider bar instVars that this handle is linked to
		const barVars = 
			getInstances(runtime.objects.uiSliderBar.getAllInstances(), 
			instance => { return (instance.instVars.ID === handle.instVars.sliderHandleAttachedBar); })
			[0].instVars;

		// will need handle instance vars
		const handleVars = handle.instVars;


		// set properties
		barVars.sliderMin = 0;

		// set sliderMax based on axis
		if (axis === 'x') { barVars.sliderMax = -(cVars.scrollerX2 - cVars.scrollerX1) + canvas.width; }
		else if (axis === 'y') { barVars.sliderMax = -(cVars.scrollerY2 - cVars.scrollerY1) + canvas.height; }

		// set the slider steps based on scrollerSpeed, but adjusted to have no remainder when divided by range
		barVars.sliderStep = 
			handleVars.scrollerSpeed + (Math.abs(barVars.sliderMax - barVars.sliderMin) % 
			handleVars.scrollerSpeed) / Math.floor(Math.abs(barVars.sliderMax - barVars.sliderMin) / 
			handleVars.scrollerSpeed);

		// set slider min
		handleVars.sliderHandleValue = barVars.sliderMin;


		// call aux function to update, hopefully can find better solution later
		Function.call("uiSliderScrollRecalculationAux [INTERNAL]", handle.uid);
	}
}


//-------------------------------------------------------------------------------


// update UI
Function.call("uiUpdate [INTERNAL]");

actions.script:
// get instances
const canvas = runtime.objects.uiCanvas.getFirstPickedInstance();
const pasteArr = Array.from(runtime.objects.uiScrollComponent.getPickedInstances());

// iterate through all objects to be pasted
for (const pasteObj of pasteArr)
{
	// now go through children
	for (const child of pasteObj.allChildren()) 
	{
		// if this child isn't in the array already...
		if (!pasteArr.some(item => item === child))
		{
			// paste this child
			pasteArr.push(child);
		}
	}
}

// sort array by zIndex
pasteArr.sort((a, b) => a.zIndex - b.zIndex);

// paste all objects
for (const pasteObj of pasteArr)
{
	// paste this obj
	canvas.pasteInstances([pasteObj]);
}

const caroVars = runtime.objects.uiCarousel.getFirstPickedInstance().instVars;

try {
	runtime.callFunction("carousel " + caroVars.ID, caroVars.carouselIndex);
} 
catch(error) {/* nothing, just ignore it*/}

const handleVars = runtime.objects.uiSliderHandle.getFirstPickedInstance().instVars;

try {
	runtime.callFunction("slider " + handleVars.ID, handleVars.sliderHandleValue);
} 
catch(error) {/* nothing, just ignore it*/}

const checkboxVars = runtime.objects.uiCheckbox.getFirstPickedInstance().instVars;

try {
	runtime.callFunction("checkbox " + checkboxVars.ID, checkboxVars.uiChecked);
} 
catch(error) {/* nothing, just ignore it*/}

try {
	runtime.callFunction("select " + 
		runtime.objects.ui.getFirstPickedInstance().instVars.ID);
} 
catch(error) {/* nothing, just ignore it*/}
